mapper.insert:
INSERT INTO `EVENTS` (
    event_cd, user_id, workspace_cd, title, description,
    start_datetime, end_datetime, color,
    is_shared, progress_status, priority, created_date
) VALUES (
    #{eventCd}, #{userId}, #{workspaceCd}, #{title}, #{description},
    #{startDatetime}, #{endDatetime}, #{color},
    #{isShared}, #{progressStatus}, #{priority}, #{createdDate}
)

mapper.update:
UPDATE `EVENTS`
title = #{title}, description = #{description}, start_datetime = #{startDatetime}, end_datetime = #{endDatetime}, color = #{color}, is_shared = #{isShared}, progress_status = #{progressStatus}, priority = #{priority},
WHERE event_cd = #{eventCd} AND user_id = #{userId}
